import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function RegisterForm({ onRegister }) {
  const [name, setName] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleRegister = async () => {
    try {
      const response = await axios.post("http://localhost:5000/register", {
        name,
        username,
        password,
      });
      console.log(response.data); // Log the API response
      if (response.status === 200) {
        setSuccessMessage("Registration successful");
        setErrorMessage("");
        onRegister();
        setName("");
        setUsername("");
        setPassword("");
      }
    } catch (error) {
      console.error("Registration error:", error); // Log registration error
      setErrorMessage("Registration failed");
      setSuccessMessage("");
    }
  };

  return (
    <div
      className="container-fluid d-flex justify-content-center align-items-center"
      style={{
        minHeight: "100vh",
        background: "linear-gradient(to right, #4a148c, #ff6f00)", // Background gradient
      }}
    >
      <div
        className="card p-4"
        style={{
          maxWidth: "400px",
          width: "100%",
          backgroundColor: "rgba(255, 255, 255, 0.8)",
          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
        }}
      >
        <h2 className="card-title text-center mb-4">New User Registration</h2>
        <input
          type="text"
          className="form-control mb-3"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="text"
          className="form-control mb-3"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          className="form-control mb-3"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        {successMessage && <p className="text-success">{successMessage}</p>}
        {errorMessage && <p className="text-danger">{errorMessage}</p>}
        <button
          className="btn btn-primary btn-block mb-3"
          onClick={handleRegister}
        >
          Register
        </button>
        <p className="text-center">
          Existing User? <Link to="/">Login</Link>
        </p>
      </div>
    </div>
  );
}

export default RegisterForm;
